/*Data for the table `StaticContents` */

insert  into `StaticContents`(`StaticContentId`,`StaticCode`,`StaticContent`,`IsSerialize`,`LastUpdated`,`LastUpdatedBy`) values (1,'ABOUT_TEXT','4impact is a Next-Generation IT services company based in Brisbane Queensland.&nbsp; We provide a range of IT services from individual consultants to complete enterprise solutions for Corporate and Government clients along the eastern seaboard of Australia.&nbsp; <br /><br />With a core team of IT professionals, backed by a vast network of partners and associates, 4impact is able to bring to bear significant and targeted professionals to solve our customer&rsquo;s information challenges. <br /><br />With a local 50 seat state-of-the-art software development studio at 370 Queen Street, as well as offshore delivery centres in Vietnam and Philippines; 4impact is able to deliver application development services across a range of technology platforms. Currently our teams are delivering a range of Insurance, Payments, eCommerce, Airline, Media and Social Networking software solutions<br /><br />',0,'2013-03-15 20:23:24',1),(2,'ABOUT_ADDRESS1','Level 1, 370 Queen Street',0,'2012-12-25 13:42:37',1),(3,'ABOUT_ADDRESS2','Brisbane, QLD 4000.',0,'2012-12-25 13:42:37',1),(4,'ABOUT_LETSTALK','YTozOntzOjQ6Im5hbWUiO3M6NDg6IjE1NyBMw6JtIFbEg24gQuG7gW4sIFAuQsOsbmggVGh14bqtbiwgUTcsIFRQLkhDTSI7czo1OiJwaG9uZSI7czoyNToiMDkwOTM2MTk2MyAtIDA5MTkgMTcgNjQ0NiI7czo1OiJlbWFpbCI7czo0ODoiIGxpZW5oZUBnaWFzdS10YW0uY29tIGhv4bq3YyBpbmZvIEBnaWFzdS10YW0uY29tIjt9',1,'2013-03-15 20:25:56',1),(5,'FOOTER_CONTACT','YTozOntzOjQ6Im5hbWUiO3M6NDg6IjE1NyBMw6JtIFbEg24gQuG7gW4sIFAuQsOsbmggVGh14bqtbiwgUTcsIFRQLkhDTSI7czo1OiJwaG9uZSI7czoyNToiMDkwOSAzNjE5NjMgLSAwOTE5IDE3NjQ0NiI7czo1OiJlbWFpbCI7czo0NzoiIGxpZW5oZUBnaWFzdS10YW0uY29tIGhv4bq3YyBpbmZvQGdpYXN1LXRhbS5jb20iO30',1,'2012-12-25 13:42:37',1),(6,'HOME_PITCH','a:3:{s:19:\"primary_pitch_line1\";s:11:\"Enterprise \";s:19:\"primary_pitch_line2\";s:16:\"Mobile Solutions\";s:12:\"second_pitch\";s:30:\"Engaging   Clever   Connected \";}',1,'2013-04-04 01:04:09',1),(7,'HOME_TECHNOLOGY','Kính gởi Quý Phụ Huynh …\r\nĐầu tiên, TTGS chúng tôi gởi lời chúc sức khỏe, hạnh phúc đến gia đình\r\nQuý Phụ Huynh!	\r\n\r\n   Ngày nay, Quý Phụ Huynh luôn bận rộn với nhiều công việc, ít có thời gian chú ý đến việc học tập của con em mình. Thấu hiểu nỗi lo lắng chính đáng đó, TTGS chúng tôi đưa ra hình thức học tập mang lại hiệu quả cao cho các em, đó là:		                              \r\nDẠY KÈM TẠI NHÀ. \r\n',0,'2013-04-02 05:41:18',1),(8,'PEOPLE_HOME_LEFT','<p>&nbsp; &nbsp;K&iacute;nh gởi <strong>Qu&yacute; Phụ Huynh</strong> &hellip;</p>\r\n\r\n<p>Đầu ti&ecirc;n, TTGS ch&uacute;ng t&ocirc;i gởi lời ch&uacute;c sức khỏe, hạnh ph&uacute;c đến gia đ&igrave;nh</p>\r\n\r\n<p>Qu&yacute; Phụ Huynh!&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; Ng&agrave;y nay, Qu&yacute; Phụ Huynh lu&ocirc;n bận rộn với nhiều c&ocirc;ng việc, &iacute;t c&oacute; thời gian ch&uacute; &yacute; đến việc học tập của con em m&igrave;nh. Thấu hiểu nỗi lo lắng ch&iacute;nh đ&aacute;ng đ&oacute;, TTGS ch&uacute;ng t&ocirc;i đưa ra h&igrave;nh thức học tập mang lại hiệu quả cao cho c&aacute;c em, đ&oacute; l&agrave;:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong><em>DẠY K&Egrave;M TẠI NH&Agrave;</em></strong>.</p>\r\n\r\n<p>Nhận dạy k&egrave;m tại nh&agrave; c&aacute;c m&ocirc;n to&aacute;n-l&iacute;-h&oacute;a-sinh-ngoại ngữ-văn&hellip;v&agrave; theo y&ecirc;u cầu của qu&yacute; phụ huynh,học sinh từ lớp 1 -12</p>\r\n\r\n<p>Với đội ngũ <strong>Gi&aacute;o vi&ecirc;n nhiều kinh nghiệm, sinh vi&ecirc;n ĐHSP </strong><strong><em>&amp; c&aacute;c trường đại học uy t&iacute;n</em></strong>; lấy phương ch&acirc;m chữ &ldquo;<strong>T&Acirc;M</strong>&rdquo; h&agrave;ng đầu , phương ph&aacute;p sư phạm hiệu quả, dễ hiểu, dạy b&aacute;m s&aacute;t chương tr&igrave;nh s&aacute;ch gi&aacute;o khoa của Bộ gi&aacute;o dục v&agrave; dạy mở rộng chương tr&igrave;nh n&acirc;ng cao, TTGS ch&uacute;ng t&ocirc;i sẽ gi&uacute;p c&aacute;c em vượt qua c&aacute;c kỳ thi quan trọng ( thi học kỳ, thi chuyển cấp, tốt nghiệp trung học phổ th&ocirc;ng v&agrave; thi đại học &hellip; ). &ldquo; <strong>Học đi đ&ocirc;i với h&agrave;nh</strong> &rdquo;, ch&uacute;ng t&ocirc;i sẽ hỗ trợ tốt nhất đề c&aacute;c em c&oacute; thể kết hợp hiệu quả cao giữa l&yacute; thuyết v&agrave; b&agrave;i tập. Ngo&agrave;i ra, ch&uacute;ng t&ocirc;i cũng thường xuy&ecirc;n b&aacute;o c&aacute;o kết quả học tập của học sinh ( mỗi th&aacute;ng một lần ) cho qu&yacute; Phụ Huynh.Sau một th&aacute;ng, qu&yacute; Phụ Huynh sẽ <strong>h&agrave;i l&ograve;ng v&igrave; sự tiến bộ</strong> của con em m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&atilde;y li&ecirc;n hệ với ch&uacute;ng t&ocirc;i để được tư vấn miễn ph&iacute; v&agrave; t&igrave;m cho con em m&igrave;nh 1 gia sư giỏi ,tận t&acirc;m &hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>BẢNG HỌC PH&Iacute; THAM KHẢO:</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong><em>Đội ngũ </em></strong><em>:&nbsp;&nbsp;&nbsp; </em>+ <strong><em>Gi&aacute;o vi&ecirc;n Kinh nghiệm</em></strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;+ <strong><em>Sinh vi&ecirc;n ĐHSP&amp;c&aacute;c trường đại học uy t&iacute;n</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Bảng học ph&iacute; <strong>sinh vi&ecirc;n</strong> /th&aacute;ng (2 buổi/tuần)</em></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\